(function(){angular.module("bookingSystem.startPage",["bookingSystem.bookingServices","bookingSystem.commonFilters"]).config(["$routeProvider",function($routeProvider){}]).controller("StartCtrl",["$rootScope","$scope","Booking","$q","Location",function($rootScope,$scope,Booking,$q,Location){var that=this;that.getEmptyBookings=function(){var deferred=$q.defer(),promise=deferred.promise;$scope.emptyBookings=Booking.countEmpty();$scope.emptyBookings.$promise.then(function(){deferred.resolve()});$scope.emptyBookings.$promise.catch(function(){$rootScope.FlashMessage={type:"error",message:"Tomma bokningstillfällen kunde inte hämtas, var god försök igen."}});return promise};that.getBookingsForToday=function(){var deferred=$q.defer(),promise=deferred.promise;$scope.bookingsForToday=Booking.queryMoreForPeriod({fromDate:that.todayDate,toDate:that.todayDate});$scope.bookingsForToday.$promise.then(function(){$scope.noBookingsForTodayFound=!$scope.bookingsForToday.length;deferred.resolve()});$scope.bookingsForToday.$promise.catch(function(){$rootScope.FlashMessage={type:"error",message:"Dagens bokningstillfällen kunde inte hämtas, var god försök igen."}});return promise};that.getFreeLocationsForToday=function(){var deferred=$q.defer(),promise=deferred.promise;$scope.locationsFreeForToday=Location.queryFreeForPeriod({fromDate:that.todayDate,toDate:that.todayDate});$scope.locationsFreeForToday.$promise.then(function(){$scope.noLocationsFreeForTodayFound=!$scope.locationsFreeForToday.length;deferred.resolve()});$scope.locationsFreeForToday.$promise.catch(function(){$rootScope.FlashMessage={type:"error",message:"Lediga lokaler för idag kunde inte hämtas, var god försök igen."}});return promise};that.todayDate=moment().format("YYYY-MM-DD");that.getEmptyBookings().then(function(){that.getBookingsForToday().then(function(){that.getFreeLocationsForToday()})})}])})();